function CircularProgressWithLabel({rating, max}) {
  const value = Math.round(rating / max * 100)
  return (
      <Box sx={{position: 'relative', display: 'inline-flex', ml: 10, mt: 2}}>
        <CircularProgress variant="determinate" value={value} size={'4em'}/>
        <Box
            sx={{
              top: 0,
              left: 0,
              bottom: 0,
              right: 0,
              position: 'absolute',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
        >
          <Typography variant="caption" component="div">
            {`${rating.toFixed(2)} / ${max}`}
          </Typography>
        </Box>
      </Box>
  );
}

function SoundQualityResult({data, loading}) {
  const {t} = useTranslation("main")

  // let data = mockedData

  function getTimeConsumptionBar(data) {
    let algoList = []
    let dataList = []
    for (let i = 0; i < availableAlgorithms.length; i++) {
      if (data[availableAlgorithms[i]]) {
        algoList.push(availableAlgorithms[i])
        dataList.push(data[availableAlgorithms[i]].time)
      }
    }
    return {
      title: [
        {
          text: t("hints.timeConsumptionTitle"),
          left: 'center'
        },
      ],
      polar: {
        radius: [30, '80%']
      },
      radiusAxis: {
        max: Math.max(...dataList)
      },
      angleAxis: {
        type: 'category',
        data: algoList,
        startAngle: 75
      },
      tooltip: {},
      series: {
        type: 'bar',
        data: dataList,
        coordinateSystem: 'polar',
        label: {
          show: true,
          position: 'middle',
          formatter: '{b}: {c}'
        }
      },
      backgroundColor: '#fff',
      animation: true
    }
  }

  function getBoxOption(title, dataset, columnNames, granu = 5) {
    return {
      title: [
        {
          text: `${t("hints.qualityResultOfAlgo")} ${title}`,
          left: 'center'
        },
      ],
      dataset: [
        {
          source: dataset
        },
        {
          transform: {
            type: 'boxplot',
          }
        },
        {
          fromDatasetIndex: 1,
          fromTransformResult: 1
        }
      ],
      tooltip: {
        trigger: 'item',
        axisPointer: {
          type: 'shadow'
        }
      },
      grid: {
        left: '10%',
        right: '10%',
        bottom: '15%'
      },
      xAxis: {
        type: 'category',
        boundaryGap: true,
        nameGap: 30,
        splitArea: {
          show: false
        },
        splitLine: {
          show: true
        },
        axisLabel: {
          formatter: function (value) {
            return columnNames[value]
          }
        }
      },
      yAxis: {
        type: 'value',
        splitArea: {
          show: true
        },
        splitLine: {
          show: true
        },
        max: Math.ceil(Math.max(
            ...dataset.reduce((prev, val) => prev.concat(val), [])
        ) / granu) * granu,
        min: Math.floor(Math.min(
            ...dataset.reduce((prev, val) => prev.concat(val), [])
        ) / granu) * granu,
      },
      series: [
        {
          name: 'boxplot',
          type: 'boxplot',
          datasetIndex: 1
        },
        {
          name: 'outlier',
          type: 'scatter',
          datasetIndex: 2
        }
      ]
    }
  }

  let timeBarOptions = null
  if (data) {
    timeBarOptions = getTimeConsumptionBar(data)
  }

  return (
      <Box sx={{mt: 10}}>
        {loading ?
            <Box sx={{textAlign: "center"}}>
              <CircularProgress/>
            </Box>
            :
            <>
              <Typography variant={"h2"} sx={{textAlign: "center"}}>
                {t("hints.qualityResultTitle")}
              </Typography>
              <Box sx={{height: 2}}> &nbsp; </Box>
              {data.bsseval &&
                  <>
                    <ReactECharts option={getBoxOption(
                        "bsseval",
                        [
                          data.bsseval.score.isr,
                          data.bsseval.score.sar,
                          data.bsseval.score.sdr
                        ],
                        ["isr", "sar", "sdr"]
                    )}/>
                    {["isr", "sar", "sdr"].map(e => {
                      return (
                          <QualityRating
                              algoName={"bsseval-" + e}
                              rating={data.bsseval.avgScore[e]}
                              max={"db"}
                              precision={0.5}
                          />
                      )
                    })}
                  </>
              }
              <Box sx={{display: "flex", flexWrap: "wrap", mt: 5}}>
                {
                  [
                    {algo: "mosnet", max: 5},
                    {algo: "srmr", max: 5},
                    {algo: "pesq", max: 5},
                    {algo: "sisdr", max: "db"},
                    {algo: "stoi", max: 1}
                  ].map(({algo, max}) => {
                    return (<>
                      {data[algo] &&
                          <Box key={algo} sx={{width: "50%"}}>
                            <ReactECharts option={getBoxOption(
                                algo,
                                [data[algo].score],
                                ["overall"],
                                (algo === "pesq" ? 1e-2 : (algo === "stoi" ? 1e-7 : 5))
                            )}/>
                            <QualityRating
                                algoName={algo}
                                rating={data[algo].avgScore}
                                max={max}
                                precision={0.5}
                            />
                            {algo !== "sisdr" &&
                                <CircularProgressWithLabel
                                    rating={data[algo].avgScore}
                                    max={max}
                                />
                            }
                            <Box sx={{height: 4, pt: 2, mt: 4}}> &nbsp; </Box>
                          </Box>
                      }
                    </>)
                  })
                }
              </Box>
              <Box sx={{height: "60vh"}}>
                <ReactECharts option={timeBarOptions} style={{height: "60vh"}}/>
              </Box>
            </>
        }
        <Box sx={{height: 4, pt: 2, mt: 4}}> &nbsp; </Box>
      </Box>
  )
}

